import React, { createContext, useReducer } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { navReducer } from "../reducers/ThemeReducer";

export const ThemeContext = createContext();

const ThemeContextProvider = (props) => {
  // Get the navigate function from React Router
  const navigate = useNavigate();

  // Function to handle navigation to the blog page
  const navigateToBlog = () => {
    navigate("/blog");
  };

  return (
    <ThemeContext.Provider value={{ services, setServices }}>
      <nav>
        <ul>
          <li>
            {/* Use onClick to navigate to "/blog" */}
            <a href="#blog" onClick={navigateToBlog}>
              Blog
            </a>
          </li>
          {/* Render other navigation items */}
        </ul>
      </nav>
      {props.children}
    </ThemeContext.Provider>
  );
};

export default ThemeContextProvider;
