import React, { useEffect, useState } from "react";
import "../stylesheets/header.css";

const Header = () => {
  const [services, setServices] = useState([
    "Software Development",
    "Web Design",
    "Penetration Testing",
    "IT Consultancy",
  ]);

  const [currentServiceIndex, setCurrentServiceIndex] = useState(0);

  useEffect(() => {
    const serviceAnimationInterval = setInterval(() => {
      if (currentServiceIndex < services.length - 1) {
        setCurrentServiceIndex(currentServiceIndex + 1);
      } else {
        setCurrentServiceIndex(0);
      }
    }, 3000); // Change service every 3 seconds

    return () => {
      clearInterval(serviceAnimationInterval);
    };
  }, [currentServiceIndex, services]);

  return (
    <header id="home">
      <div id="landing-header">
        <div className="header" id="typing-animation">
          <h1>
            <p className="typing-animation">{services[currentServiceIndex]}</p>
          </h1>
          <h1>EMPOWERING</h1>
          <h1>LIVES</h1>

          <a href="#about-us" className="btn btn-primary">
            Learn About Us
          </a>
          <a href="#our-services" className="btn btn-secondary">
            Explore Our Services
          </a>
        </div>
      </div>
    </header>
  );
};

export default Header;
