import React, { useState } from "react";
import articles from "../blog/article-content";
import { Link } from "react-router-dom";
import "../blog/article.css"; // Import your CSS file

function Blog() {
  const [expandedArticles, setExpandedArticles] = useState({});

  const toggleExpand = (articleName) => {
    setExpandedArticles((prevState) => ({
      ...prevState,
      [articleName]: !prevState[articleName],
    }));
  };

  return (
    <div className="blog-container">
      <h2>Blog</h2>
      <ul className="article-list">
        {articles.map((article, index) => (
          <li key={index} className="article-item">
            <h3>{article.title}</h3>
            <p className="article-content">
              {expandedArticles[article.name]
                ? article.content.join("\n\n")
                : article.content[0]}
            </p>
            <button
              className="read-more-button"
              onClick={() => toggleExpand(article.name)}>
              {expandedArticles[article.name] ? "Read Less" : "Read More"}
            </button>
            <Link to={`/blog/${article.name}`} className="read-full-link">
              Read Full Article
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Blog;
