import React, { useState } from "react";
import articles from "../blog/Articles";
import "../blog/article";

function Blog() {
  const [expandedArticles, setExpandedArticles] = useState({});

  const toggleExpand = (articleName) => {
    setExpandedArticles((prevState) => ({
      ...prevState,
      [articleName]: !prevState[articleName],
    }));
  };

  return (
    <div className="App">
      <div id="page-body">
        <h2>Blog</h2>
        <ul>
          {articles.map((article, index) => (
            <li key={index}>
              <h3>{article.title}</h3>
              <p>
                {expandedArticles[article.name]
                  ? article.content.join("\n\n")
                  : article.content[0]}
              </p>
              <button onClick={() => toggleExpand(article.name)}>
                {expandedArticles[article.name] ? "Read Less" : "Read More"}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Blog;
